#!/usr/bin/env python3# Exercise: Presidents# Write a program to:# (1) Load the data from presidents.txt into a dictionary.# (2) Print the years the greatest and least number of presidents were alive.#     (between 1732 and 2016 (inclusive))#     Ex.#       'least = 2015'#       'John Doe'#       'most = 2015'#       'John Doe, Jane Doe, John Adams, and Jane Adams'# Bonus: Confirm there are no ties. If there is a tie print like so:#     Ex.#       'least = 1900, 2013-2015'#       'John Doe (1900)'#       'Jane Doe (2013-2015)'#       'most = 1900-1934, 2013'#       'John Doe, Jane Doe, John Adams, and Jane Adams (1900-1933)'#       'Sally Doe, Billy Doe, Mary Doe, and Cary Doe (1934)'#       'Alice Doe, Bob Doe, Zane Doe, and Yi Do (2013)'############################################################################### Imports# Bodydef get_presidents_dict():    presidents_dict = {}    with open("presidents.txt","r") as presidents_file:        for each_line in presidents_file:            line_interpreter = each_line.strip().split(',')            if(line_interpreter[2] == "None"):                line_interpreter[2] = "2016"            for each_year in range(int(line_interpreter[1]), int(line_interpreter[2])):                presidents_dict[each_year] = presidents_dict.get(each_year,[]) + [line_interpreter[0]]    return presidents_dictdef create_year_wise_alive_number_dict():    presidents_dict = get_presidents_dict()    year_alive_dict = {}    for each_year in sorted(presidents_dict):        no_of_presidents = len(presidents_dict[each_year])        year_alive_dict[no_of_presidents] = year_alive_dict.get(no_of_presidents,[]) + [each_year]    return year_alive_dictdef print_least_most_presidents():    presidents_dict = get_presidents_dict()    year_alive_dict = create_year_wise_alive_number_dict()    least = year_alive_dict[sorted(year_alive_dict, reverse = True)[-1]]    most = year_alive_dict[sorted(year_alive_dict,  reverse = True)[0]]    #least printing    least_str = "least = "    if(len(least) == 1):        least_str += "{}".format(" ".join(least))    else:        least_str += "{}".format(least[0])        add_str = ""        for year_idx in range(1,len(least)):            if(least[year_idx]-least[year_idx-1] == 1):                add_str = "-{}".format(least[year_idx])            else:                least_str += add_str + ", {}".format(least[year_idx])    print(least_str + "\n")    least_names = ""    if(len(least) == 1):        print("{} ({})".format(" ".join(presidents_dict[least[0]]),least[0]))    else:        least_names = "{} ({}".format(" , ".join(presidents_dict[least[0]]),least[0])        add_str = ""        no_print_flag = True        for year_idx in range(1,len(least)):            if((presidents_dict[least[year_idx]] > presidents_dict[least[year_idx-1]]) - (presidents_dict[least[year_idx]] < presidents_dict[least[year_idx-1]]) == 0):                add_str = "-{}".format(least[year_idx])            else:                print(least_names + add_str +")\n")                least_names = "{} ({}".format(" , ".join(presidents_dict[least[year_idx]]),least[year_idx])        print(least_names + ")\n")    #most printing    most_str = "\n\nmost = "    if(len(most) == 1):        most_str += "{}".format(" ".join(most))    else:        most_str += "{}".format(most[0])        add_str = ""        for year_idx in range(1,len(most)):            if(most[year_idx]-most[year_idx-1] == 1):                add_str = "-{}".format(most[year_idx])            else:                most_str += add_str + ", {}".format(most[year_idx])    print(most_str + "\n\n")    most_names = ""    if(len(most) == 1):        print("{} ({})".format(" ".join(presidents_dict[most[0]]),most[0]))    else:        most_names = "{} ({}".format(" , ".join(presidents_dict[most[0]]),most[0])        add_str = ""        no_print_flag = True        for year_idx in range(1,len(most)):            if((presidents_dict[most[year_idx]] > presidents_dict[most[year_idx-1]]) - (presidents_dict[most[year_idx]] < presidents_dict[most[year_idx-1]]) == 0):                add_str = "-{}".format(most[year_idx])            else:                print(most_names + add_str +")\n")                most_names = "{} ({}".format(" , ".join(presidents_dict[most[year_idx]]),most[year_idx])        print(most_names + ")\n")##############################################################################def main():  # CALL YOUR FUNCTION BELOW    print_least_most_presidents()if __name__ == '__main__':    main()